function() {
  //Get the text of the element which was clicked
  var clickText = {{Click Text}};
  //Get last four characters of click text  
  var lastFourCharsInClickText=clickText.slice(-4);
  //Set action appropriately based on last four chars (Drupal websites include "LESS" or "MORE" automatically at the end of the element text.)
  if (lastFourCharsInClickText=='LESS') {
  	var returnValue = "close";
  } else if (lastFourCharsInClickText=='MORE') {
  	var returnValue = "open";
  } else {
    //Last four characters are neither "LESS" nor "MORE". Probably not a Mason standard Drupal website. We can't determine whether this was an open or close action. Return "indeterminate".
  	var returnValue = "indeterminate";
  }
  return returnValue;
}

function() {
  //Get the text of the element which was clicked
  var clickText = {{Click Text}};
  //Get last four characters of click text  
  var lastFourCharsInClickText=clickText.slice(-4);
  //Are the last four characters "MORE" or "LESS" (as they would be for our Mason standard Drupal websites)?  
  if (lastFourCharsInClickText=='MORE' || lastFourCharsInClickText=='LESS') {
    //If so, strip them
  	var returnValue = clickText.slice(0, clickText.length-4);
  } else {
    //Text value doesn't include "LESS" or "MORE" indicator. Therefore, return the unedited click text.
  	var returnValue = clickText;
  }
  return returnValue;
}

function() {
	// What: IsInternalLink - Function to determine whether the GTM Page Hostname (built-in variable) occurs within the Click URL . If it does, return true; otherwise return false.
	// Who: https://www.simoahava.com/gtm-tips/10-useful-custom-javascript-tricks/#4
	// When: 20170622

	// Does the page hostname occurs within the click URL . If it does, return true; otherwise return false.
	return {{Click URL}}.indexOf({{Page Hostname}}) > -1;
}

function() {
	// What: isLocalWebsite - Function to determine whether the GTM Page Hostname (built-in variable) for the current page represents the local website hostname (as defined by the GTM Local Website Hostname custom constant variable).
	// Who: Jan Macario
  	// When: 20180208
  	//
  	//Change log:
  	//20170622: initial regex to check for the precise hostname, optionally prepended with a "www."
  	//20180208: modified regex make it less restrictive

    // Build regex string
    var strRegex = "{{Hostname - local}}$";

    // Create regex variable
    var regex = new RegExp(strRegex,"gi");

    //Test page hostname against regex
    if (regex.test("{{Page Hostname}}")) { return "true"; }

    //If no match, return false
        return "false";
}
